import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import '../styles/BillingInvoicesPage.css';

const BillingInvoicesPage = () => {
  const [invoices, setInvoices] = useState([]);

  useEffect(() => {
    const dummyInvoices = [
      { id: 1, item: 'Basic Plan', date: '2025-05-01', amount: 'â‚¹500', status: 'Paid' },
      { id: 2, item: 'Medium Plan', date: '2025-04-01', amount: 'â‚¹1000', status: 'Paid' },
      { id: 3, item: 'Premium Plan', date: '2025-03-01', amount: 'â‚¹1500', status: 'Failed' },
    ];
    setInvoices(dummyInvoices);
  }, []);

  return (
    <>
      <Navbar />
      <div className="invoice-container">
        <h2 className="invoice-title">ðŸ’³ Billing & Invoices</h2>
        {invoices.map((invoice) => (
          <div className="invoice-card" key={invoice.id}>
            <div className="invoice-details">
              <p className="invoice-item"><strong>{invoice.item}</strong></p>
              <p>Date: {invoice.date}</p>
              <p>Status: 
                <span className={invoice.status === 'Paid' ? 'status-paid' : 'status-failed'}>
                  {invoice.status}
                </span>
              </p>
              <p>Amount: <strong>{invoice.amount}</strong></p>
            </div>
            <button className="download-btn" onClick={() => alert(`Downloading invoice for ${invoice.item}`)}>
              Download
            </button>
          </div>
        ))}
      </div>
    </>
  );
};

export default BillingInvoicesPage;
