import React, { useEffect, useState } from 'react';

const BillingInvoicesPage = () => {
  const [invoices, setInvoices] = useState([]);

  useEffect(() => {
    const dummyInvoices = [
      { id: 1, date: '2025-05-01', amount: 'â‚¹499.00', status: 'Paid' },
      { id: 2, date: '2025-04-01', amount: 'â‚¹499.00', status: 'Paid' },
      { id: 3, date: '2025-03-01', amount: 'â‚¹499.00', status: 'Failed' },
    ];
    setInvoices(dummyInvoices);
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ textAlign: 'center' }}>ðŸ’³ Billing & Invoices</h2>

      <table style={{
        width: '80%',
        margin: '20px auto',
        borderCollapse: 'collapse',
        backgroundColor: '#fff',
        borderRadius: '10px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.05)'
      }}>
        <thead>
          <tr style={{ borderBottom: '2px solid #ddd' }}>
            <th style={{ padding: '10px' }}>Date</th>
            <th style={{ padding: '10px' }}>Amount</th>
            <th style={{ padding: '10px' }}>Status</th>
            <th style={{ padding: '10px' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice.id} style={{ borderBottom: '1px solid #eee', textAlign: 'center' }}>
              <td style={{ padding: '10px' }}>{invoice.date}</td>
              <td style={{ padding: '10px' }}>{invoice.amount}</td>
              <td style={{ padding: '10px' }}>{invoice.status}</td>
              <td style={{ padding: '10px' }}>
                <button
                  style={{
                    padding: '6px 12px',
                    backgroundColor: 'royalblue',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                  }}
                  onClick={() => alert('Download started')}
                >
                  Download
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BillingInvoicesPage;
