import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import '../styles/UserProfileCard.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserProfile = () => {
  const [user, setUser] = useState({});
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ firstName: '', lastName: '', email: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData && userData !== "undefined") {
      try {
        const parsedUser = JSON.parse(userData);
        setUser(parsedUser);
        setFormData({
          firstName: parsedUser.firstName || '',
          lastName: parsedUser.lastName || '',
          email: parsedUser.email || ''
        });
      } catch (err) {
        console.error("Failed to parse user:", err);
        localStorage.removeItem("user");
      }
    }
  }, []);

  const handleEditToggle = () => setEditMode(!editMode);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/api/users/${user.id}`, formData);
      const updatedUser = { ...user, ...formData };
      localStorage.setItem("user", JSON.stringify(updatedUser));
      setUser(updatedUser);
      setEditMode(false);
      alert('Profile updated successfully');
    } catch (error) {
      console.error("Update failed:", error);
      alert('Update failed');
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      try {
        await axios.delete(`http://localhost:5000/api/users/${user.id}`);
        localStorage.removeItem("user");
        alert('Account deleted successfully');
        navigate('/register');
      } catch (error) {
        console.error("Delete failed:", error);
        alert('Delete failed');
      }
    }
  };

  return (
    <>
      <Navbar />
      <div className="card">
        <div className="card__img">
          <img
            src={user.avatar || "https://i.ibb.co/4pDNDk1/avatar.png"}
            alt="avatar"
            className="avatar"
          />
        </div>
        <div className="card__body">
          <h2>{user.firstName || 'First'} {user.lastName || 'Last'}</h2>
          {editMode ? (
            <>
              <input type="text" name="firstName" value={formData.firstName} onChange={handleInputChange} />
              <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} />
              <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
              <button onClick={handleUpdate}>Save</button>
              <button onClick={handleEditToggle}>Cancel</button>
            </>
          ) : (
            <>
              <p><strong>First Name:</strong> {user.firstName || 'N/A'}</p>
              <p><strong>Last Name:</strong> {user.lastName || 'N/A'}</p>
              <p><strong>Email:</strong> {user.email || 'N/A'}</p>
              <p><strong>ID:</strong> {user.id || 'N/A'}</p>

              <div className="card__buttons">
                <button className="btn-outline" onClick={handleEditToggle}>Edit</button>
                <button className="btn-solid" onClick={handleDelete}>Delete</button>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default UserProfile;
