import React, { useState } from 'react';
import axios from 'axios';
import '../styles/RegisterPage.css';

function RegisterPage() {
  const [Firstname, setFirstname] = useState('');
  const [Lastname, setLastname] = useState('');
  const [Email, setEmail] = useState('');
  const [Password, setPassword] = useState('');
  const [ConfirmPassword, setConfirmPassword] = useState('');
  const [notification, setNotification] = useState(null); // ✅

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (Password !== ConfirmPassword) {
      setNotification({ type: 'error', message: 'Passwords do not match!' });
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/users/register', {
        firstName: Firstname,
        lastName: Lastname,
        email: Email,
        password: Password,
      });

      setNotification({ type: 'success', message: 'Registration successful! You can now log in.' });

      // Optionally clear form
      setFirstname('');
      setLastname('');
      setEmail('');
      setPassword('');
      setConfirmPassword('');
    } catch (error) {
      setNotification({ type: 'error', message: 'Registration failed. Please try again.' });
      console.error(error.response?.data || error.message);
    }
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <p className="title">Register </p>
      <p className="message">Signup now and get full access to our app. </p>

      {notification && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      <div className="flex">
        <label>
          <input
            required
            type="text"
            className="input"
            value={Firstname}
            onChange={(e) => setFirstname(e.target.value)}
          />
          <span>Firstname</span>
        </label>

        <label>
          <input
            required
            type="text"
            className="input"
            value={Lastname}
            onChange={(e) => setLastname(e.target.value)}
          />
          <span>Lastname</span>
        </label>
      </div>

      <label>
        <input
          required
          type="email"
          className="input"
          value={Email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <span>Email</span>
      </label>

      <label>
        <input
          required
          type="password"
          className="input"
          value={Password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <span>Password</span>
      </label>

      <label>
        <input
          required
          type="password"
          className="input"
          value={ConfirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <span>Confirm password</span>
      </label>

      <button className="submit" type="submit">Submit</button>
      <p className="signin">Already have an account? <a href="/login">Signin</a></p>
    </form>
  );
}

export default RegisterPage;
