import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/RegisterPage.css';
import Notification from '../components/Notification';

function RegisterPage() {
  const [Firstname, setFirstname] = useState('');
  const [Lastname, setLastname] = useState('');
  const [Email, setEmail] = useState('');
  const [Password, setPassword] = useState('');
  const [ConfirmPassword, setConfirmPassword] = useState('');
  const [notification, setNotification] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (Password !== ConfirmPassword) {
      setNotification({ type: 'error', message: "Passwords do not match!" });
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/users/register', {
        firstName: Firstname,
        lastName: Lastname,
        email: Email,
        password: Password,
      });
      setNotification({ type: 'success', message: 'User registered successfully!' });
      setTimeout(() => navigate('/login'), 1500);
    } catch (error) {
      console.error('Registration failed:', error.response?.data || error.message);
      setNotification({ type: 'error', message: 'Registration failed.' });
    }
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      {notification && <Notification type={notification.type} message={notification.message} />}
      <p className="title">Register </p>
      <p className="message">Signup now and get full access to our app. </p>
      <div className="flex">
        <label>
          <input required placeholder="" type="text" className="input" value={Firstname} onChange={(e) => setFirstname(e.target.value)} />
          <span>Firstname</span>
        </label>

        <label>
          <input required placeholder="" type="text" className="input" value={Lastname} onChange={(e) => setLastname(e.target.value)} />
          <span>Lastname</span>
        </label>
      </div>

      <label>
        <input required placeholder="" type="email" className="input" value={Email} onChange={(e) => setEmail(e.target.value)} />
        <span>Email</span>
      </label>

      <label>
        <input required placeholder="" type="password" className="input" value={Password} onChange={(e) => setPassword(e.target.value)} />
        <span>Password</span>
      </label>

      <label>
        <input required placeholder="" type="password" className="input" value={ConfirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        <span>Confirm password</span>
      </label>

      <button className="submit" type="submit">Submit</button>
      <p className="signin">Already have an account? <a href="/login">Signin</a></p>
    </form>
  );
}

export default RegisterPage;
