import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
function Loginpage() {
  const [email, setEmail] = useState(''); // email is the current value and setemail is for the update it 
  const [password,setpassword] = useState('');
  const navigate = useNavigate (); // this is a navigation function
  const handlesubmit  = (e) => {  
    e.preventDefault();    // stop page reload 
  const userdata ={
    name:"tarun aditya",
    email:email,
    role:"user",
  };
  localStorage.setItem("user",JSON.stringify(userdata)); //store data in local 
   navigate('/User-profile');
  };
  return(       // render the login from ui 
    <div>
      <h2>login</h2>
      <form onSubmit ={handlesubmit}>
        <input
         type = "email"
         placeholder='email'
         value={email}
         onChange = {(e) => setEmail(e.target.value)}
         />
         <br/><br/>
         <input
         type = "password"
         placeholder='passsword'
         value={password}
         onChange = {(e) => setpassword(e.target.value)}
         />
         <br/><br/>
         <button type = "submit">login</button>    
      </form>
    </div>
  );
}
export default Loginpage;
