import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import '../styles/UserProfileCard.css';

const UserProfile = () => {
  const [user, setUser] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: ''
  });

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser && storedUser !== "undefined") {
      const parsed = JSON.parse(storedUser);
      setUser(parsed.user || {});
      setFormData({
        firstName: parsed.user?.firstName || '',
        lastName: parsed.user?.lastName || '',
        email: parsed.user?.email || ''
      });
    }
  }, []);

  const handleEdit = () => setEditing(true);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/users/${user.id}`, formData);
      const updatedUser = { ...user, ...formData };
      localStorage.setItem("user", JSON.stringify({ user: updatedUser }));
      setUser(updatedUser);
      setEditing(false);
      alert("Profile updated successfully");
    } catch (err) {
      alert("Failed to update user");
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this account?")) {
      try {
        await axios.delete(`http://localhost:5000/api/users/${user.id}`);
        localStorage.removeItem("user");
        window.location.href = "/register";
      } catch (err) {
        alert("Failed to delete user");
      }
    }
  };

  return (
    <>
      <Navbar />
      <div className="card">
        <div className="card__img">
          <img
            src={"https://i.ibb.co/4pDNDk1/avatar.png"}
            alt="avatar"
            className="avatar"
          />
        </div>
        <div className="card__body">
          <h2>{user.firstName || 'First'} {user.lastName || 'Last'}</h2>
          {editing ? (
            <>
              <p><strong>First Name:</strong> <input name="firstName" value={formData.firstName} onChange={handleChange} /></p>
              <p><strong>Last Name:</strong> <input name="lastName" value={formData.lastName} onChange={handleChange} /></p>
              <p><strong>Email:</strong> <input name="email" value={formData.email} onChange={handleChange} /></p>
            </>
          ) : (
            <>
              <p><strong>First Name:</strong> {user.firstName || 'N/A'}</p>
              <p><strong>Last Name:</strong> {user.lastName || 'N/A'}</p>
              <p><strong>Email:</strong> {user.email || 'N/A'}</p>
            </>
          )}
          <p><strong>ID:</strong> {user.id || 'N/A'}</p>

          <div className="card__buttons">
            {editing ? (
              <button className="btn-outline" onClick={handleSave}>Save</button>
            ) : (
              <button className="btn-outline" onClick={handleEdit}>Edit</button>
            )}
            <button className="btn-solid" onClick={handleDelete}>Delete</button>
          </div>
        </div>
      </div>
    </>
  );
};

export default UserProfile;
